components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    {{#if product}}<dt class="productView-info-name">{{lang 'products.bulk_pricing.title'}}</dt>{{/if}}
    <dd class="productView-info-value">
        <a href="{{product.url}}#bulk_pricing"
           {{#unless is_ajax }}data-reveal-id="modal-bulk-pricing"{{/unless}}>
            {{lang 'products.bulk_pricing.view'}}
        </a>
    </dd>
    <div id="modal-bulk-pricing" class="modal modal--small" data-reveal>
        <div class="modal-header">
            <h2 class="modal-header-title">{{lang 'products.bulk_pricing.modal_title'}}</h2>
            {{> components/common/modal/modal-close-btn}}
        </div>
        <div class="modal-body">
            <p>{{lang 'products.bulk_pricing.instructions'}}</p>
            <ul>
                {{#each bulk_discount_rates}}
                <li>
                    {{lang 'products.bulk_pricing.range' min=min max=max}}
                    {{#if type '===' 'percent'}}
                    {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                    {{else if type '===' 'fixed'}}
                    {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                    {{else if type '===' 'price'}}
                    {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                    {{/if}}
                </li>
                {{/each}}
            </ul>
        </div>
    </div>
{{/if}}

components/products/product-aria-label:
{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
{{lang 'category.shop_by_price_range_aria' from=price.price_range.min.with_tax.formatted to=price.price_range.max.with_tax.formatted}}
{{else}}
{{lang 'category.shop_by_price_range_aria' from=price.price_range.min.without_tax.formatted to=price.price_range.max.without_tax.formatted}}
{{/and}}
{{else}}
{{#if price.with_tax}}
{{#if price.non_sale_price_with_tax}}
{{theme_settings.pdp-non-sale-price-label}}{{price.non_sale_price_with_tax.formatted}},
{{theme_settings.pdp-sale-price-label}}{{price.with_tax.formatted}}
{{else}}
{{price.with_tax.formatted}}                        
{{/if}}
{{else}}
{{#if price.non_sale_price_without_tax}}
{{theme_settings.pdp-non-sale-price-label}}{{price.non_sale_price_without_tax.formatted}},
{{theme_settings.pdp-sale-price-label}}{{price.without_tax.formatted}}
{{else}}
{{price.without_tax.formatted}}
{{/if}}
{{/if}}
{{/and}}

pages/account/wishlist-details:
{{#partial "page"}}
<div class="account" data-pagination-wishlist>

    {{#if wishlist.is_editable}}
        {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    {{/if}}
    <h2 class="page-heading">{{lang 'account.wishlists.view_heading' name=wishlist.name}}</h2>
    {{#if wishlist.is_editable}}
        {{> components/account/navigation account_page='wishlists'}}
    {{/if}}

    {{#if wishlist.is_editable}}
        {{#if wishlist.is_public '>' 0}}
            <div class="panel share-wishlist">
                <div class="panel-body">
                    <h5>{{lang 'account.wishlists.share_intro'}}</h5>
                    <input class="form-input" value="{{settings.base_url}}{{wishlist.share_url}}" readonly>
                </div>
            </div>
        {{/if}}
    {{/if}}

    {{#if wishlist.items}}
        {{> components/account/wishlist-item-list show_remove=wishlist.is_editable}}
    {{else}}
        {{> components/common/alert/alert-info (lang 'account.wishlists.empty_wishlist')}}
    {{/if}}

</div>
{{/partial}}
{{> layout/base}}
